def nb_handle (array)
    count = array.count
    puts "#{count}"
end 

def shortest_handle(array)
    shortest = array.min_by {|handle| handle.length}
    puts "Shortest handle is: #{shortest} and is #{shortest.length - 1} characters"
end

def i_got_five_on_it(array)
    number = array.count {|handle| handle.length - 1 <= 5}
    puts "You have #{number} which got 5 on it"
end

def capital_punishment(array)
    number_cap = array.count {|handle| handle[1] =~ /[A-Z]/}
    puts "you have #{number_cap} handles starting with a capital letter"
end

def order_alpha(array)
    array_sorted = array.sort_by {|handle| handle[1]} 
    puts array_sorted.inspect
end

def order_size(array)
    array_sorted = array.sort_by {|handle| handle.length} 
    puts array_sorted.inspect
end

def find_the_precious(array)
    position = array.index ("@epenser")
    puts "#{position} away from mordor frodo"
end

def max_length(array)
    max = array.max_by {|i| i.length}
    max_length = max.length + 1
end

def count_and_sort(array)
    i = 1
    while i <= max_length(array)
    count = array.count {|handle| handle.length + 1 == i}
    puts " you have #{count} elements with #{i} characters"
    i += 1
    end
end

